mapper.update:
update bu.wq_appointment_review
set appointment_id = #{appointmentId,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=INTEGER},
  score = #{score,jdbcType=REAL},
  review = #{review,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, appointment_id, user_id, score, review
from bu.wq_appointment_review
where id = #{id,jdbcType=INTEGER}
