mapper.update:
update bu.wq_course_material_unlock_record
set course_material_id = #{courseMaterialId,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=INTEGER},
  unlock_time = #{unlockTime,jdbcType=TIMESTAMP},
  study_points_cost = #{studyPointsCost,jdbcType=SMALLINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, course_material_id, user_id, unlock_time, study_points_cost
from bu.wq_course_material_unlock_record
where id = #{id,jdbcType=INTEGER}
