mapper.update:
update bu.wq_discussion_group_member
set discussion_group_id = #{discussionGroupId,jdbcType=INTEGER},
  member_id = #{memberId,jdbcType=INTEGER},
  action = #{action,jdbcType=VARCHAR},
  action_time = #{actionTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, discussion_group_id, member_id, action, action_time
from bu.wq_discussion_group_member
where id = #{id,jdbcType=INTEGER}
