mapper.delete:
delete from bu.wq_favorite_course
where user_id = #{userId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_favorite_course (user_id, course_id, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{courseId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_favorite_course
set create_time = #{createTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.select:
select user_id, course_id, create_time
from bu.wq_favorite_course
where user_id = #{userId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
