mapper.delete:
delete from bu.wq_favorite_course_material
where user_id = #{userId,jdbcType=INTEGER}
  and course_material_id = #{courseMaterialId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_favorite_course_material (user_id, course_material_id, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{courseMaterialId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_favorite_course_material
set create_time = #{createTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and course_material_id = #{courseMaterialId,jdbcType=INTEGER}

mapper.select:
select user_id, course_material_id, create_time
from bu.wq_favorite_course_material
where user_id = #{userId,jdbcType=INTEGER}
  and course_material_id = #{courseMaterialId,jdbcType=INTEGER}
