mapper.delete:
delete from bu.wq_favorite_professor
where user_id = #{userId,jdbcType=INTEGER}
  and professor_id = #{professorId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_favorite_professor (user_id, professor_id, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{professorId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_favorite_professor
set create_time = #{createTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and professor_id = #{professorId,jdbcType=INTEGER}

mapper.select:
select user_id, professor_id, create_time
from bu.wq_favorite_professor
where user_id = #{userId,jdbcType=INTEGER}
  and professor_id = #{professorId,jdbcType=INTEGER}
