mapper.delete:
delete from bu.wq_favorite_thread
where user_id = #{userId,jdbcType=INTEGER}
  and thread_id = #{threadId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_favorite_thread (user_id, thread_id, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{threadId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_favorite_thread
set create_time = #{createTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and thread_id = #{threadId,jdbcType=INTEGER}

mapper.select:
select user_id, thread_id, create_time
from bu.wq_favorite_thread
where user_id = #{userId,jdbcType=INTEGER}
  and thread_id = #{threadId,jdbcType=INTEGER}
