mapper.update:
update bu.wq_online_event_member
set online_event_id = #{onlineEventId,jdbcType=INTEGER},
  member_id = #{memberId,jdbcType=INTEGER},
  action = #{action,jdbcType=VARCHAR},
  action_time = #{actionTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, online_event_id, member_id, action, action_time
from bu.wq_online_event_member
where id = #{id,jdbcType=INTEGER}
