mapper.delete:
delete from bu.wq_professor_course
where professor_id = #{professorId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.update:
update bu.wq_professor_course
set create_by = #{createBy,jdbcType=TIMESTAMP},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where professor_id = #{professorId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}

mapper.select:
select professor_id, course_id, create_by, create_time
from bu.wq_professor_course
where professor_id = #{professorId,jdbcType=INTEGER}
  and course_id = #{courseId,jdbcType=INTEGER}
