mapper.update:
update bu.wq_study_point_history
set user_id = #{userId,jdbcType=INTEGER},
  action_log = #{actionLog,jdbcType=VARCHAR},
  amount = #{amount,jdbcType=SMALLINT},
  action_time = #{actionTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, action_log, amount, action_time
from bu.wq_study_point_history
where id = #{id,jdbcType=INTEGER}
