mapper.delete:
delete from bu.wq_tutor_subject
where tutor_id = #{tutorId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_tutor_subject (tutor_id, subject_id, create_time
  )
values (#{tutorId,jdbcType=INTEGER}, #{subjectId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_tutor_subject
set create_time = #{createTime,jdbcType=TIMESTAMP}
where tutor_id = #{tutorId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}

mapper.select:
select tutor_id, subject_id, create_time
from bu.wq_tutor_subject
where tutor_id = #{tutorId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}
