mapper.delete:
delete from bu.wq_user_subject
where user_id = #{userId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}

mapper.insert:
insert into bu.wq_user_subject (user_id, subject_id, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{subjectId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update bu.wq_user_subject
set create_time = #{createTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}

mapper.select:
select user_id, subject_id, create_time
from bu.wq_user_subject
where user_id = #{userId,jdbcType=INTEGER}
  and subject_id = #{subjectId,jdbcType=INTEGER}
